import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ModalController } from '@ionic/angular';
import { UserService } from '../user.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit {

  loginForm: FormGroup<any> | FormGroup<{
    username: FormControl<string>;
    password: FormControl<string>;
  }>;
  loginStatus:any;
  message: string ;

  constructor(private service: UserService, public fb: FormBuilder, private modalCtrl: ModalController) { }
  ngOnInit(): void {
    this.loginForm = this.fb.group({
      username: new FormControl('', Validators.email),
      password: new FormControl('', Validators.nullValidator),
     });
  }

  onSubmit(form: FormGroup) {
    return this.login(this.loginForm);
  }

  cancel() {
    return this.modalCtrl.dismiss(null,'cancel');
  }
  confirm() {
    return this.modalCtrl.dismiss(this.login(this.loginForm),'confirm');
  }

  login(form: FormGroup){
    console.log('UserLoginComponent::login is called');
    if(form.valid){
      this.loginStatus = this.service.register(form)
      .subscribe((resp) => {
        console.log('UserLoginComponent::login resultatet af resp.body ',resp);
        return resp;
      });

    }
  }
}
