import { Component } from '@angular/core';
import { ModalController } from '@ionic/angular';
import { EditLogEntryFormComponent } from '../logEntries/edit-log-entry-form/edit-log-entry-form.component';
import { UserRegisterComponent } from '../user/user-register/user-register.component';
import { LoginComponent } from '../user/login/login.component';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  constructor(private modalCtrl: ModalController) {}

  async openModal() {
    const modal = await this.modalCtrl.create({
      component: EditLogEntryFormComponent,
    });
    modal.present();

    const { data, role } = await modal.onWillDismiss();

    if (role === 'confirm') {
      console.log('data saved', data);
    }
  }

  async login() {
    const modal = await this.modalCtrl.create({
      component: LoginComponent,
    });
    modal.present();

    const { data, role } = await modal.onWillDismiss();
    console.log('data from modal data:',data,'role:',role);
    if (role === 'confirm') {
      console.log('data saved', data);
    }
  }

  async register() {
    const modal = await this.modalCtrl.create({
      component: UserRegisterComponent,
    });
    modal.present();

    const { data, role } = await modal.onWillDismiss();

    if (role === 'confirm') {
      console.log('data saved', data);
    }
  }


}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
     DriverLog
    </ion-title>
    <ion-buttons slot="end">
      <ion-button expand="block" (click)="openModal()" > <ion-icon name="add-outline"></ion-icon> </ion-button>
    </ion-buttons>
    <ion-buttons slot="end">
      <ion-button expand="block" (click)="login()" > <ion-icon name="person-outline"></ion-icon> </ion-button>
    </ion-buttons>

  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">

  <!--app-edit-log-entry-form></app-edit-log-entry-form-->
  <app-list-log-entries></app-list-log-entries>

</ion-content>
