import { Component, OnInit } from '@angular/core';
import { User } from '../user';
import {
  Form,
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
import { ModalController } from '@ionic/angular';
import { UserService } from '../user.service';
import {OverlayEventDetail} from '@ionic/core/components';

@Component({
  selector: 'app-user-register',
  templateUrl: './user-register.component.html',
  styleUrls: ['./user-register.component.scss'],
})
export class UserRegisterComponent implements OnInit {

  registerForm: FormGroup<any> | FormGroup<{
    username: FormControl<string>;
    password: FormControl<string>;
    retypePassword: FormControl<string>;
    name: FormControl<string>;
  }>;

  registerStatus: any;
  message: string;


  constructor(private service: UserService, public fb: FormBuilder, private modalCtrl: ModalController) {}

  onSubmit(form: FormGroup) {
    return this.modalCtrl.dismiss(this.register(this.registerForm),'Registrerer');
  }

  cancel() {
    throw new Error('Method not implemented.');
  }
  confirm() {
    return this.modalCtrl.dismiss(this.register(this.registerForm),'Registrer');
  }


  ngOnInit(): void {
    this.registerForm = this.fb.group({
      username: new FormControl('', Validators.email),
      password: new FormControl('', Validators.nullValidator),
      retypePassword: new FormControl('', Validators.nullValidator),
      name: new FormControl('', Validators.nullValidator),
    });
  }

  register(form: FormGroup) {
    console.log('UserRegisterComponent::register is called');
    if(form.valid){
      this.registerStatus = this.service.register(form)
      .subscribe((resp) => {
        console.log('UserReggeisterComponent::register resultatet af resp.body ',resp);
        return resp;
      });

    }

  }

  onWillDismiss(event: Event) {
    const ev = event as CustomEvent<OverlayEventDetail<string>>;

    console.info('paramter event er ',event);
    console.info('paramter ev er ',ev);

    if (ev.detail.role === 'Registrer') {
      this.message = `Hello, ${ev.detail.data}!`;
    }
  }
}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-button (click)="cancel()">cancel</ion-button>
    </ion-buttons>
    <ion-title>Indtast bruger oplysninger </ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="confirm()">Registrer</ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <form [formGroup]="registerForm" (ngSubmit)="onSubmit(registerForm)">
    <ion-item>
      <ion-label position="floating">E-Mail <ion-text color="danger">*</ion-text></ion-label>
      <ion-input required formControlName="username" type="text"></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Password <ion-text color="danger">*</ion-text></ion-label>
      <ion-input required formControlName="password" type="password"></ion-input>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Gentag Password <ion-text color="danger">*</ion-text></ion-label>
      <ion-input required formControlName="retypePassword" type="password"></ion-input>
      <ion-note slot="helper">Gentag password</ion-note>
      <ion-note slot="error">Password er ikke ens</ion-note>
    </ion-item>
    <ion-button block color="primary" type="submit" [disabled]="registerForm.invalid && (registerForm.value.password === registerForm.value.retypePassword)">
      Register
    </ion-button>
  </form>
</ion-content>
